/*--------------------
Shop Card
---------------------*/
.shop-card {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 350px;
  background: #f5f5f5;
  box-shadow: 0 10px 20px rgba(0, 0, 0, 0.3);
  overflow: hidden;
  border-radius: 5px;
  padding: 25px;
  text-align: center;
  z-index: 2;
}
.shop-card figure {
  margin: 0;
  padding: 0;
  overflow: hidden;
  outline: none !important;
}
.shop-card figure img {
  margin: -95px 0 -60px;
  width: 100%;
}
.shop-card .title {
  font-weight: 900;
  text-transform: uppercase;
  font-size: 30px;
  color: #23211f;
  margin-bottom: 5px;
}
.shop-card .desc {
  font-size: 17px;
  opacity: 0.8;
  margin-bottom: 3px;
}
.shop-card .cta {
  padding: 20px 20px 5px;
}
.shop-card .cta::after {
  content: "";
  display: table;
  clear: both;
}
.shop-card .price {
  float: left;
  color: #ff3100;
  font-size: 22px;
  font-weight: 900;
  padding-top: 2px;
  transition: color 0.3s ease-in-out;
  margin-top: 4px;
}
.shop-card .btn {
  position: relative;
  z-index: 1;
  float: right;
  display: inline-block;
  font-size: 13px;
  font-weight: 900;
  text-transform: uppercase;
  color: #ff3100;
  padding: 12px 18px;
  cursor: pointer;
  transition: all 0.3s ease-in-out;
  line-height: 0.95;
  border: none;
  background: none;
  outline: none;
  border: 1px solid #ff3100;
  border-radius: 20px;
  overflow: hidden;
}
.shop-card .btn .bg {
  width: 101%;
  height: 101%;
  display: block !important;
  z-index: -1;
  opacity: 0;
  transition: all 0.3s ease-in-out;
  background: linear-gradient(135deg, #a61322, #d33f34);
}
.shop-card .btn:hover {
  color: #fff !important;
  border: 1px solid transparent !important;
}
.shop-card .btn:hover .bg {
  opacity: 1;
}

/*--------------------
Slick Dots
---------------------*/
.slick-dots {
  bottom: -30px;
}
.slick-dots a {
  position: relative;
  display: block;
  width: 16px;
  height: 16px;
}
.slick-dots span {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 6px;
  height: 6px;
  border-radius: 50%;
}
.slick-dots circle {
  fill: none;
  stroke-width: 1;
  stroke-linecap: round;
  stroke-dasharray: 39 39;
  stroke-dashoffset: 39;
  transition: all 0.9s ease-in-out;
  transition: stroke-dashoffset 0.5s;
}
.slick-dots .slick-active circle {
  stroke-dashoffset: 0;
}

/*--------------------
Background
--------------------*/
.bg {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 100%;
  height: 100%;
  background: linear-gradient(15deg, #d33f34 50%, #a61322 50.1%);
  z-index: 1;
  display: none;
}

.the-most {
  position: fixed;
  z-index: 1;
  bottom: 0;
  left: 0;
  width: 50vw;
  max-width: 200px;
  padding: 10px;
}
.the-most img {
  max-width: 100%;
}

javascript:
function background(c1, c2) {
    return {
      background: '-moz-linear-gradient(15deg, ' + c1 + ' 50%, ' + c2 + ' 50.1%)',
      background: '-o-linear-gradient(15deg, ' + c1 + ', ' + c2 + ' 50.1%)',
      background: '-webkit-linear-gradient(15deg, ' + c1 + ' 50%, ' + c2 + ')',
      background: '-ms-linear-gradient(15deg, ' + c1 + ' 50%, ' + c2 + ' 50.1%)',
      background: 'linear-gradient(15deg, ' + c1 + ' 50%,' + c2 + ' 50.1%)'
    }
  }

  function changeBg(c1, c2) {
    $('div.bg').css(background(c1, c2)).fadeIn(700, function() {
      $('body').css(background(c1, c2));
      $('.bg').hide();
    })
    $('span.bg').css({
      background: '-moz-linear-gradient(135deg, ' + c1 + ', ' + c2 + ')',
      background: '-o-linear-gradient(135deg, ' + c1 + ', ' + c2 + ')',
      background: '-webkit-linear-gradient(135deg, ' + c1 + ', ' + c2 + ')',
      background: '-ms-linear-gradient(135deg, ' + c1 + ', ' + c2 + ')',
      background: 'linear-gradient(135deg, ' + c1 + ',' + c2 + ')'
    });
  }

  $slider = $('.slider');

  $slider.slick({
    arrows: false,
    dots: true,
    infinite: true,
    speed: 600,
    fade: true,
    focusOnSelect: true,
    customPaging: function(slider, i) {
      var color = $(slider.$slides[i]).data('color').split(',')[1];
      return '<a><svg width="100%" height="100%" viewBox="0 0 16 16"><circle cx="8" cy="8" r="6.215" stroke="' + color + '"></circle></svg><span style="background:' + color + '"></span></a>';
    }
  }).on('beforeChange', function(event, slick, currentSlide, nextSlide) {
    colors = $('figure', $slider).eq(nextSlide).data('color').split(',');
    color1 = colors[0];
    color2 = colors[1];
    $('.price, .btn').css({
      color: color1
    });
    changeBg(color1, color2);
    $('.btn').css({
      borderColor: color2
    });
  });

