@import url("https://fonts.googleapis.com/css2?family=Raleway:wght@400&display=swap");
*,
*::before,
*::after {
  padding: 0;
  margin: 0 auto;
  box-sizing: border-box;
}

body {
  font-family: "Raleway", sans-serif;
  background-color: #eee;
  color: #111;
  display: grid;
  place-items: center;
  font-size: 16px;
  perspective: 1500px;
  overflow: hidden;
}
body *:not(:empty) {
  transform-style: preserve-3d;
}

.hidden {
  position: fixed;
  top: -100vh;
  left: -100vw;
  appearance: none;
  background: none;
  border: none;
}

.scene {
  position: relative;
  transform: translateY(15em);
}

.stateLabels {
  position: absolute;
  top: -14em;
  display: flex;
  gap: 2em;
  transform: translateX(-50%);
}
.stateLabels::before {
  content: "csStickman";
  position: absolute;
  top: -4em;
  width: 100%;
  text-align: center;
  font-size: 1.25em;
}

.stateLabel {
  width: 5em;
  position: relative;
  text-align: center;
  cursor: pointer;
}
.stateLabel::after {
  content: "";
  position: absolute;
  left: 50%;
  bottom: 0;
  width: 0%;
  height: 1px;
  transform: translateX(-50%);
  background-color: #0007;
  transition: width 0.5s;
}

#state1:checked ~ .scene [for="state1"]::after {
  width: 100%;
}

#state2:checked ~ .scene [for="state2"]::after {
  width: 100%;
}

#state3:checked ~ .scene [for="state3"]::after {
  width: 100%;
}

#state2:checked ~ .scene .head {
  animation: headWalk 2s infinite ease-in-out;
}
@keyframes headWalk {
  0%,
  100% {
    transform: rotateZ(-5deg);
  }
  50% {
    transform: rotateZ(5deg);
  }
}
#state2:checked ~ .scene .torso {
  animation: torsoWalk 2s infinite ease-in-out;
}
@keyframes torsoWalk {
  0%,
  50%,
  100% {
    transform: translateY(0.8em);
  }
  25%,
  75% {
    transform: translateY(0em);
  }
}
#state2:checked ~ .scene .arm {
  animation: armWalk 2s var(--armDelay, 0s) infinite ease-in-out;
}
@keyframes armWalk {
  0%,
  100% {
    transform: rotateZ(var(--armAngle)) rotateX(-20deg);
  }
  50% {
    transform: rotateZ(var(--armAngle)) rotateX(20deg);
  }
}
#state2:checked ~ .scene .arm.left {
  --armDelay: -1.9s;
}
#state2:checked ~ .scene .arm.right {
  --armDelay: -0.9s;
}
#state2:checked ~ .scene .hand {
  animation: handWalk 2s var(--armDelay, 0s) infinite ease-in-out;
}
@keyframes handWalk {
  0%,
  100% {
    transform: rotateZ(var(--handAngle)) rotateX(0deg);
  }
  75% {
    transform: rotateZ(var(--handAngle)) rotateX(20deg);
  }
