css:
html,
body {
  width: 100%;
  height: 100%;
}

body {
  display: flex;
  align-items: center;
  justify-content: center;
  position: relative;
  overflow: hidden;
  -webkit-animation: bg-anim 15s ease infinite;
  animation: bg-anim 15s ease infinite;
  background-image: linear-gradient(-45deg, #4571d8, #21586f, #666b7a, #2b5ea5);
  background-size: 400% 400%;
}

.container {
  position: relative;
  width: 263px;
  max-height: 525px;
  height: 100vh;
  overflow: hidden;
  border-radius: 20px;
  box-shadow: 0 30px 50px rgba(0, 0, 0, 0.3);
  z-index: 1;
}

.device {
  position: absolute;
}

.layer1,
.layer2 {
  position: absolute;
  right: 0;
  top: 0;
  width: 100%;
  height: 100%;
  transform: scale(1.01);
}

.layer2 {
  opacity: 0;
  transition: 4s;
}

.layer1 {
  opacity: 1;
  transition: 4s;
}

.moon {
  transform: translate(-50px, 230px);
  transition: 4s;
}

.rising-star {
  transform: translate(225px, -160px);
  transition: 4s;
}

.sun {
  transition: 5s;
}

.cloud1,
.cloud2,
.cloud3,
.cloud4 {
  transition: 4s;
}

.light-mode .layer2 {
  opacity: 1;
}
.light-mode .moon,
.light-mode .rising-star {
  transform: none;
  transition-delay: 1s;
}
.light-mode .sun {
  transform: translate(64px, -290px);
}
.light-mode .cloud1 {
  transform: translateX(-140px);
}
.light-mode .cloud2 {
  transform: translateX(-100px);
}
.light-mode .cloud3 {
  transform: translateX(-200px);
}
.light-mode .cloud4 {
  transform: translateX(-180px);
}

.toggle {
  width: 45px;
  height: 24px;
  position: absolute;
  right: 8px;
