    height: fit-content; /* Allow the container to shrink to fit its content */
    min-height: initial; /* Override user's 'min-height: 100vh' */
    padding: 0; /* Remove user's internal padding to not expand the component's visible bounding box */
    gap: 0; /* Remove user's gap if there were multiple items; irrelevant for a single item */
    background: transparent; /* Make container background transparent so only the 'drop' is visually distinct */
    /* User's original 'display: flex; justify-content: center; align-items: center;' on .container
       will ensure the .drop inside it is centered within the container itself. */
}

css:
body {
    margin: 0;
}
.button {
    display: inline-block;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    min-width: 100px;
    padding: 22px 33px;
    font-family: "Lobster", cursive;
    font-size: 26px;
    line-height: 26px;
    text-decoration: none;
    color: #fff;
    text-shadow: 0 1px 2px rgba(0, 0, 0, 0.75);
    background: #5e0d0c;
    outline: none;
    border-radius: 15px;
    border: 1px solid #4c0300;
    box-shadow: inset 1px 1px 0px rgba(255, 255, 255, 0.25), /* highlight */ inset 0 0 6px #a23227,
        /* inner glow */ inset 0 80px 80px -40px #ac3223, /* gradient */ 1px 1px 3px rgba(0, 0, 0, 0.75); /* shadow */

    position: relative;
    overflow: visible; /* IE9 & 10 */
    -webkit-transition: 500ms linear;
    -moz-transition: 500ms linear;
    -o-transition: 500ms linear;
    transition: 500ms linear;
}

.button::before {
    content: "";
    display: block;
    position: absolute;
    top: -7px;
    left: -3px;
    right: 0;
    height: 23px;
    background: url(data:image/png;base64,)
            no-repeat 0 0,
        url(data:image/png;base64,)
            no-repeat 50% 0,
        url(data:image/png;base64,)
            no-repeat 100% 0;
}
.button:hover {
    background: #a61715;
    text-shadow: 0 1px 2px rgba(0, 0, 0, 0.75), 0 0 40px #fff;
    box-shadow: inset 1px 1px 0px rgba(255, 255, 255, 0.25), /* highlight */ inset 0 0 6px #da3b2c,
        /* inner glow */ inset 0 80px 80px -40px #dd4330, /* gradient */ 1px 1px 3px rgba(0, 0, 0, 0.75); /* shadow */
}
.button:focus {
    outline: none; /*FF*/
}
.button:active {
    box-shadow: inset 1px 1px 0px rgba(255, 255, 255, 0.25), /* highlight */ inset 0 0 6px #da3b2c,
        /* inner glow */ inset 0 80px 80px -40px #dd4330, /* gradient */ 0px 1px 0px rgba(255, 255, 255, 0.25); /* shadow */

    -webkit-transition: 50ms linear;
    -moz-transition: 50ms linear;
    -o-transition: 50ms linear;
    transition: 50ms linear;
}

/* other styles */
html,
body {
    width: 100%;
    height: 100%;
}
.bg {
    width: 100%;
    height: 100%;
    display: table;
    background: url(data:image/svg+xml;base64,);
    background: -moz-radial-gradient(center, ellipse cover, #d35448 0%, #4a110c 100%); /* FF3.6+ */
    background: -webkit-gradient(
        radial,
        center center,
        0px,
        center center,
        100%,
        color-stop(0%, #d35448),
        color-stop(100%, #4a110c)
    ); /* Chrome,Safari4+ */
    background: -webkit-radial-gradient(center, ellipse cover, #d35448 0%, #4a110c 100%); /* Chrome10+,Safari5.1+ */
    background: -o-radial-gradient(center, ellipse cover, #d35448 0%, #4a110c 100%); /* Opera 12+ */
    background: -ms-radial-gradient(center, ellipse cover, #d35448 0%, #4a110c 100%); /* IE10+ */
    background: radial-gradient(ellipse at center, #d35448 0%, #4a110c 100%); /* W3C */
}
.centerer {
    display: table-cell;
    vertical-align: middle;
    text-align: center;
    /* noise image */
    background-image: url(data:image/png;base64,);
}

