css:
* {
    border: 0;
    box-sizing: border-box;
    margin: 0;
    padding: 0;
}
:root {
    --bg: #e3e4e8;
    --bgT: #e3e4e800;
    --fg: #17181c;
    --focused: #b6cafb;
    --focusRing: #86a6f9;
    --unchecked: #ffffff;
    --checked: #255ff4;
    font-size: calc(20px + (30 - 20) * (100vw - 320px) / (1280 - 320));
}
body,
input {
    font: 1em/1.5 system-ui, -apple-system, sans-serif;
}
body {
    background: var(--bg);
    color: var(--fg);
    display: flex;
    height: 100vh;
}
form {
    margin: auto;
    overflow: hidden;
    position: relative;
    padding: 0.75em 0;
}
form:before,
form:after,
.circles,
.circle {
    position: absolute;
}
form:before,
form:after {
    content: "";
    left: 0;
    width: 100%;
    height: 0.5em;
    z-index: 1;
}
form:before {
    background: linear-gradient(var(--bg), var(--bgT));
    top: 0;
}
form:after {
    background: linear-gradient(var(--bgT), var(--bg));
    bottom: 0;
}
input {
    position: fixed;
    top: -1.5em;
    left: -1.5em;
}
