    place-items: center;
}
label,
input[type="checkbox"] {
    cursor: pointer;
}
label {
    display: inline-flex;
    align-items: center;
    margin-bottom: 0.75em;
    position: relative;
    -webkit-tap-highlight-color: transparent;
}
input[type="checkbox"],
input[type="checkbox"]:before,
input[type="checkbox"]:after {
    width: 1.5rem;
    height: 1.5rem;
}
input[type="checkbox"],
input[type="checkbox"]:before {
    background: var(--checkBg);
    border-radius: 0.2em;
    box-shadow: 0 0 0 1px var(--checkBorder) inset;
}
input[type="checkbox"]:before,
input[type="checkbox"]:after {
    display: block;
    position: absolute;
    top: 0;
    left: 0;
}
input[type="checkbox"] {
    margin-right: 0.75em;
    -webkit-appearance: none;
    appearance: none;
}
input[type="checkbox"] + span {
    animation: brighten var(--dur) linear;
}
input[type="checkbox"]:before {
    animation: unstrike var(--dur) linear;
    content: "";
    transform-origin: 0 50%;
    z-index: 1;
}
input[type="checkbox"]:after {
    color: var(--primary);
    content: "\2713";
    font-size: 1.5em;
    line-height: 1;
    text-align: center;
}
input[type="checkbox"]:focus {
    outline: transparent;
}
input[type="checkbox"]:focus + span {
    text-decoration: underline;
}
input[type="checkbox"]:checked + span {
    animation-name: dim;
    color: var(--fgDim);
}
input[type="checkbox"]:checked:before {
    animation-name: strike;
    background: var(--fgDim);
    border-radius: 0;
    box-shadow: 0 0 0 1px var(--fgDim) inset;
    transform: translateX(2.25em) scale(1, 0.05);
    width: calc(100% - 2.25em);
}
input[type="checkbox"].pristine:before,
input[type="checkbox"].pristine + span {
    animation: none;
}
