  }
}

@keyframes line-animation {
  0% {
    -webkit-transform: scaleX(0);
            transform: scaleX(0);
  }
  15% {
    -webkit-transform: scaleX(0);
            transform: scaleX(0);
  }
  20%, 25% {
    -webkit-transform: scaleX(1);
            transform: scaleX(1);
    top: calc( 100% - 2px);
  }
  50% {
    -webkit-transform: scaleX(1);
            transform: scaleX(1);
    top: 0px;
  }
  70% {
    -webkit-transform: scaleX(0.2);
            transform: scaleX(0.2);
    top: 0px;
  }
  80%, 100% {
    -webkit-transform: scaleX(0.2);
            transform: scaleX(0.2);
    top: 0px;
  }
}

